INSERT INTO note (id,type_id,user_id,content) VALUES (164,0,'jpilla','Amaury de Montfort, fils et successeur de Simon de Montfort († 1218). Il cède au roi de France en 1224 ses droits dans le Midi.');
INSERT INTO note (id,type_id,user_id,content) VALUES (165,0,'jpilla','Aymeri, vicomte de Narbonne de 1193 à 1239, habile à monnayer son ralliement à Simon de Montfort et à l’archevêque Arnaud.');
INSERT INTO note (id,type_id,user_id,content) VALUES (166,0,'jpilla','L’expression d’homme lige désigne ici, dans son acception classique, le vassal lié à son seigneur principal par un lien féodal primant sur ses obligations vis-à-vis d’autres seigneurs.');
INSERT INTO note (id,type_id,user_id,content) VALUES (167,0,'jpilla','Traditionnellement identifié avec Lézignan-la-Cèbe (Hérault, cant. Montagnac), près de Pézenas ; pourrait être aussi Lézignan-Corbières (Aude, ch.-l. cant.), sur la route de Carcassonne à Narbonne.');
INSERT INTO note (id,type_id,user_id,content) VALUES (168,0,'jpilla','Guy de Montfort, frère de Simon († 1228).');
INSERT INTO note (id,type_id,user_id,content) VALUES (169,0,'jpilla','La monnaie de Melgueil, auj. Mauguio (Hérault, ch.-l. cant.), est constituée de deniers « melgoriens » ; on parle logiquement de « livres de [deniers] melgoriens ».');
INSERT INTO note (id,type_id,user_id,content) VALUES (170,0,'jpilla','« Aide et conseil » sont les deux grandes obligations liant le vassal à son seigneur ; le terme « faveur », complétant le triptyque, est plus rare.');
INSERT INTO note (id,type_id,user_id,content) VALUES (171,0,'jpilla','Les cardinaux-prêtres portent explicitement le nom du « titre » (= église paroissiale de Rome) dont ils sont les desservants primitifs (par opposition, on parle simplement de cardinal-diacre « de » telle église, ou du cardinal-évêque « de » tel siège suburbicaire). Bertrand Savelli, neveu du pape Innocent III, cardinal-prêtre du titre des Saints-Jean-et-Paul (1216-1222), était légat pontifical en plusieurs provinces et diocèses du Midi de la France depuis le début de 1217.');
