INSERT INTO note (id,type_id,user_id,content) VALUES (179,0,'jpilla','L’expression d’homme lige désigne ici, dans son acception classique, le vassal lié à son seigneur principal par un lien féodal primant sur ses obligations vis-à-vis d’autres seigneurs.');
INSERT INTO note (id,type_id,user_id,content) VALUES (180,0,'jpilla','Traditionnellement identifié avec Lézignan-la-Cèbe (Hérault, cant. Montagnac), près de Pézenas ; pourrait être aussi Lézignan-Corbières (Aude, ch.-l. cant.), sur la route de Carcassonne à Narbonne.');
INSERT INTO note (id,type_id,user_id,content) VALUES (181,0,'jpilla','Guy de Montfort, frère de Simon († 1228).');
INSERT INTO note (id,type_id,user_id,content) VALUES (182,0,'jpilla','La monnaie de Melgueil, auj. Mauguio (Hérault, ch.-l. cant.), est constituée de deniers « melgoriens » ; on parle logiquement de « livres de [deniers] melgoriens ».');
INSERT INTO note (id,type_id,user_id,content) VALUES (183,0,'jpilla','« Aide et conseil » sont les deux grandes obligations liant le vassal à son seigneur ; le terme « faveur », complétant le triptyque, est plus rare.');
