INSERT INTO note (id,type_id,user_id,content) VALUES (201,0,'jpilla','trobar : " Quelque soit l’origine (durement discutée) du mot trobar, il est clair qu’il faut en retenir, outre ce qu’il désigne, l’art des troubadours et ses productions, l’idée de découverte, qu’on retrouve dans la proximité du français trouver.…Par le trobar se dévoile la nature de l’amour, d’où il vient, ce qu’il est, qui l’éprouve, pour qui et la joie. Trouver, trobar, c’est trouver l’ amour, c’est trouver d’amour, trobar d’amor. " (Jacques Roubaud, La fleur inverse. Essai sur l’art formel des troubadours, Paris, 1986, p. 185-186).');
INSERT INTO note (id,type_id,user_id,content) VALUES (202,0,'jpilla','évêché de Gévaudan : évêché de Mende ; le pays de Gévaudan correspond à peu près à l’actuel département de la Lozère.');
