INSERT INTO note (id,type_id,user_id,content) VALUES (209,0,'jpilla','Loys : Le futur Louis XI, détenteur du Dauphiné en tant que fils aîné du roi de France, a porté une grande attention à la province, qu’il a en particulier activement administrée après s’être brouillé avec son père en 1446.');
INSERT INTO note (id,type_id,user_id,content) VALUES (210,0,'jpilla','daulphin de Viennoys : Acquis par le roi de France Philippe VI en 1343, le Dauphiné de Viennois, terre d’Empire, passe dès 1344 au futur Jean II. La règle s’observe jusqu’en 1461 de le confier au fils aîné du roi de France. Le maintien des institutions régionales se marque aussi dans le domaine diplomatique, avec l’usage d’un sceau (le « sceau dauphin ») et d’une chancellerie propres.');
INSERT INTO note (id,type_id,user_id,content) VALUES (211,0,'jpilla','conte de Valentinoys et de Dioys :Éléments complémentaires de la titulature delphinale à compter de 1419, date à laquelle le Valentinois (région de Valence) et une partie du Diois (région de Die) sont rattachés au Dauphiné ; ces terres correspondaient à peu près à la partie de l’actuel département de la Drôme qui ne relevait pas à l’origine du Dauphiné.');
INSERT INTO note (id,type_id,user_id,content) VALUES (212,0,'jpilla','Granhan : Grignan (Drôme, ch.-l. cant.).');
INSERT INTO note (id,type_id,user_id,content) VALUES (213,0,'jpilla','accort et traictié : L’échange dont il va être question a été conclu le 25 mai (E. Pilot de Thorey, Catalogue des actes du dauphin Louis II…, Grenoble, 1899-1911, n° 455, qui ignore le présent acte).');
INSERT INTO note (id,type_id,user_id,content) VALUES (214,0,'jpilla','Grillen : Grillon, aujourd’hui dans le Vaucluse (cant. Valréas), est à 5 km au sud-est de Grignan. Le dauphin Louis s’était engagé dès le 13 mai à remettre la seigneurie au pape.');
INSERT INTO note (id,type_id,user_id,content) VALUES (215,0,'jpilla','a la terre : Comprendre « avec la terre ».');
INSERT INTO note (id,type_id,user_id,content) VALUES (216,0,'jpilla','Marsenne : Marsanne (Drôme, ch.-l. cant.).');
INSERT INTO note (id,type_id,user_id,content) VALUES (217,0,'jpilla','peage de Laine et des empses de Savasse : Leyne (Drôme, cant. Marsanne, comm. Savasse) était un ancien château des comtes de Valentinois ; supprimé au XVIIe siècle, le péage dit « de Leyne et des anses de Savasse » est attesté dès 1207.');
INSERT INTO note (id,type_id,user_id,content) VALUES (218,0,'jpilla','Hugues de Verdun : Personnage non retrouvé ; mais plus tard une lettre du roi Louis XI (éd. J. Vaesen, t. III, p. 1) cite un « Jeannot de Sainte-Camelle », écuyer d’écurie du roi, implanté à Montpellier. On peut donc identifier ici Verdun avec Verdun-en-Lauragais (Aude, cant. Castelnaudary).');
INSERT INTO note (id,type_id,user_id,content) VALUES (219,0,'jpilla','Sainte Camelle : Sainte-Camelle (Aude, cant. Salles-sur-l’Hers).');
INSERT INTO note (id,type_id,user_id,content) VALUES (220,0,'jpilla','Monteil Aymart : Montélimar (Drôme, ch.-l. cant.). On sait d’après les actes que le dauphin Louis y réside au moins du 16 mai au 29 juin 1447.');
INSERT INTO note (id,type_id,user_id,content) VALUES (221,0,'jpilla','Bochetel : Jean Bochetel, secrétaire (depuis 1439 au moins) et fidèle serviteur du dauphin Louis, apparaît comme contrôleur des finances du Dauphiné en décembre 1447 ; il sert ensuite le roi Louis XI jusqu’à sa mort en 1470.');
