INSERT INTO note (id,type_id,user_id,content) VALUES (222,0,'jpilla','M(athildis) comitissa Bolonie : Mahaut de Dammartin, comtesse de Boulogne, avait successivement épousé Philippe Hurepel (bâtard de Philippe Auguste, † 1234, dont naît Jeanne), puis Alphonse de Portugal (neveu par sa mère de Blanche de Castille).');
INSERT INTO note (id,type_id,user_id,content) VALUES (223,0,'jpilla','Alfonso : Fils du roi de Portugal Afonso II († 1223) et frère cadet du roi alors régnant, Sancho II ; celui-ci déposé en 1245, Alphonse gagnera le Portugal comme régent avant d’y devenir roi en 1248 sous le nom d’Afonso III.');
INSERT INTO note (id,type_id,user_id,content) VALUES (224,0,'jpilla','parisiensium : Génitif pluriel de rigueur en latin, car denariorum est sous-entendu (mais l’usage s’est perdu en français contemporain, qui parle de « livres parisis » plutôt que de « livres de parisis »).');
INSERT INTO note (id,type_id,user_id,content) VALUES (225,0,'jpilla','libratis : Une « livrée de terre » est une unité abstraite de mesure, qui désigne la capacité de percevoir une livre de revenus annuels assis sur la terre, quel que soit le mode de détention (posséder une terre de ce rapport, détenir une rente de ce montant assise sur une terre, etc.). Il y a naturellement autant de « livrées » que de genre de monnaie, parisis, tournois, etc. ; les textes en ancien français parlent ainsi parfois de « livrée attournois » (= ad turonenses), etc.');
INSERT INTO note (id,type_id,user_id,content) VALUES (226,0,'jpilla','litteras suas patentes : voir la partie synthèse du commentaire diplomatique.');
INSERT INTO note (id,type_id,user_id,content) VALUES (227,0,'jpilla','M(athildis) comitissa Bolonie : Mahaut de Dammartin, comtesse de Boulogne, avait successivement épousé Philippe Hurepel (bâtard de Philippe Auguste, † 1234, dont naît Jeanne), puis Alphonse de Portugal (neveu par sa mère de Blanche de Castille).');
INSERT INTO note (id,type_id,user_id,content) VALUES (228,0,'jpilla','Alfonso : Fils du roi de Portugal Afonso II († 1223) et frère cadet du roi alors régnant, Sancho II ; celui-ci déposé en 1245, Alphonse gagnera le Portugal comme régent avant d’y devenir roi en 1248 sous le nom d’Afonso III.');
INSERT INTO note (id,type_id,user_id,content) VALUES (229,0,'jpilla','parisiensium : Génitif pluriel de rigueur en latin, car denariorum est sous-entendu (mais l’usage s’est perdu en français contemporain, qui parle de « livres parisis » plutôt que de « livres de parisis »).');
