INSERT INTO note (id,type_id,user_id,content) VALUES (274,0,'jpilla','Morinensis : Thérouanne (à l’origine, cité des Morins), siège d’évêché jusqu’en 1553, date où la ville est rasée avant que le diocèse ne soit partagé entre trois nouveaux sièges (Saint-Omer, Ypres et Boulogne) ; auj. village du Pas-de-Calais, cant. Aire-sur-la-Lys.');
INSERT INTO note (id,type_id,user_id,content) VALUES (275,0,'jpilla','rege Franc’ : La titulature latine, ici abrégée, pourrait être restituée aussi bien rex Francorum (si l’auteur suit l’usage de la chancellerie royale) que rex Francie (qui traduit “naturellement” l’expression française correspondante, roi de France). Il s’agit de Philippe VI, monté sur le trône en 1328.');
INSERT INTO note (id,type_id,user_id,content) VALUES (276,0,'jpilla','Johannis de Hautebruiere : La traduction pose moins de problème que l’identification. Le personnage peut être sans doute confondu avec le Johannes de Altabr’ qui apparaît comme comptable royal (mais pas davantage qualifié) dans un compte perdu de 1334 (Charles-Victor Langlois, Inventaire d’anciens comptes royaux dressés par Robert Mignon sous le règne de Philippe de Valois, Paris, 1899, append. II, p. 376, n° XXXII).');
INSERT INTO note (id,type_id,user_id,content) VALUES (277,0,'jpilla','Johannis de Hautebruiere : La traduction pose moins de problème que l’identification. Le personnage peut être sans doute confondu avec le Johannes de Altabr’ qui apparaît comme comptable royal (mais pas davantage qualifié) dans un compte perdu de 1334 (Charles-Victor Langlois, Inventaire d’anciens comptes royaux dressés par Robert Mignon sous le règne de Philippe de Valois, Paris, 1899, append. II, p. 376, n° XXXII).');
INSERT INTO note (id,type_id,user_id,content) VALUES (278,0,'jpilla','parisiensium forcium : Comprendre « livres [de deniers] parisis forts », d’où l’emploi impératif (et quasi toujours suivi dans les textes de l’époque) du génitif pluriel. Le qualificatif de « [deniers] forts » (fortes, inforciati…), par opposition aux « [deniers] faibles » (debiles), renvoie de façon générique à une monnaie renforcée (dans son poids et/ou son titre), de plus fort pouvoir libératoire ; il s’agit ici de la “bonne monnaie” émise au début du règne de Philippe VI (mise en circulation à Pâques 1330).');
INSERT INTO note (id,type_id,user_id,content) VALUES (279,0,'jpilla','Fienles : Fiennes, Pas-de-Calais, cant. Guînes.');
INSERT INTO note (id,type_id,user_id,content) VALUES (280,0,'jpilla','Tingry : Tingry, Pas-de-Calais, cant. Samer.');
INSERT INTO note (id,type_id,user_id,content) VALUES (281,0,'jpilla','guerra Flandrensi : La “guerre de Flandre” ici visée a vu Philippe VI soutenir le comte Louis Ier contre les échevinages révoltés de Bruges et d’Ypres ; elle a culminé dans la grande victoire royale de Cassel (1328).');
INSERT INTO note (id,type_id,user_id,content) VALUES (282,0,'jpilla','Morinensis : Thérouanne (à l’origine, cité des Morins), siège d’évêché jusqu’en 1553, date où la ville est rasée avant que le diocèse ne soit partagé entre trois nouveaux sièges (Saint-Omer, Ypres et Boulogne) ; auj. village du Pas-de-Calais, cant. Aire-sur-la-Lys.');
INSERT INTO note (id,type_id,user_id,content) VALUES (283,0,'jpilla','rege Franc’ : La titulature latine, ici abrégée, pourrait être restituée aussi bien rex Francorum (si l’auteur suit l’usage de la chancellerie royale) que rex Francie (qui traduit “naturellement” l’expression française correspondante, roi de France). Il s’agit de Philippe VI, monté sur le trône en 1328.');
