INSERT INTO note (id,type_id,user_id,content) VALUES (339,0,'jpilla','Le millésime ne peut être exprimé que dans le style (capétien) de Pâques ; la conversion 1304 (nouveau style) cadre d’ailleurs avec la titulature de l’ancien sénéchal de Toulouse, qui était encore en fonctions au tout début 1304.');
