INSERT INTO note (id,type_id,user_id,content) VALUES (399,0,'jpilla','discretus vir : Titre de courtoisie, mot à mot " homme qui a du discernement " (donc, par défaut, qui n’est pas noble, nobilis vir).');
INSERT INTO note (id,type_id,user_id,content) VALUES (400,0,'jpilla','magister : Le titre de " maître " peut renvoyer à plusieurs réalités de qualification professionnelle ; il est ici très vraisemblablement lié à la détention d’un grade obtenu dans une école de droit.');
INSERT INTO note (id,type_id,user_id,content) VALUES (401,0,'jpilla','Bartholomei : Patronyme au génitif singulier : Arnaud [fils/descendant] de Barthélemy ; la forme latine donne dans le Midi des patronymes comme Barthomieu ou Bartholomé (on a choisi arbitrairement la première dans la traduction).');
INSERT INTO note (id,type_id,user_id,content) VALUES (402,0,'jpilla','jurisperitus : Qualificatif savant, d’empreinte romaine, des praticiens du droit, passés par les écoles, mais en principe sans le doctorat.');
INSERT INTO note (id,type_id,user_id,content) VALUES (403,0,'jpilla','exibitorem : Incise signifiant que le procureur aura à exhiber sa procuration. L’expression lator presentium, " porteur des présentes [lettres] ", sera ensuite plus fréquente.');
INSERT INTO note (id,type_id,user_id,content) VALUES (404,0,'jpilla','Bitterim : On pourrait aussi développer " Bitterras " : les deux formes Bitteris, -is (sg.) et Biterre, -arum (pl., d’où Bitterris> " [à] Béziers ") sont attestées dans les textes médiévaux.');
INSERT INTO note (id,type_id,user_id,content) VALUES (405,0,'jpilla','P. : Pierre de Montbrun, archevêque de Narbonne de 1272 à 1286.');
INSERT INTO note (id,type_id,user_id,content) VALUES (406,0,'jpilla','prelati : Le terme de " prélat " (" placé au dessus ") semble désigner ici de façon assez vague le groupe de tous ceux qui ont des responsabilités, un pouvoir de gestion et de juridiction au sein des diocèses : archidiacres, doyens de chrétienté, supérieurs de maisons religieuses. Le terme de " pères " est plus lâche encore, qui désigne tous les participants du concile (" pères conciliaires "). La double expression semble indiquer que ce concile provincial ne réunissait que le haut de la hiérarchie, à la différence des conciles diocésains qui en principe regroupent aussi tous les curés.');
INSERT INTO note (id,type_id,user_id,content) VALUES (407,0,'jpilla','date : En 1277, la Pentecôte tombe le dimanche 16 mai ; l’acte est donc du mercredi 19 mai 1277.');
