INSERT INTO note (id,type_id,user_id,content) VALUES (431,0,'jpilla','Issu du grand lignage génois Fieschi, Innocent IV a été élu pape le 25 juin 1243, et consacré le 28 juin 1243.');
INSERT INTO note (id,type_id,user_id,content) VALUES (432,0,'jpilla','La chancellerie apostolique réserve le terme de « frères » aux évêques (mais la mention d’une décision prise « de consilio fratrum nostrorum » renvoie aux cardinaux) ; tous les autres destinataires et bénéficiaires sont des « fils », presque toujours « chers » (« très chers » pour les souverains).');
INSERT INTO note (id,type_id,user_id,content) VALUES (433,0,'jpilla','La lettre est adressée impersonnellement au roi de France, ce que soulignent les deux points qui précèdent le nom. Il s’agit, daprès la date, de Louis IX.');
