INSERT INTO note (id,type_id,user_id,content) VALUES (516,0,'jpilla','L’ordinal n’est donné que par la bulle de plomb. En l’absence de celle-ci, il faudrait croiser itinéraire pontifical, traits paléographiques et diplomatiques, données historiques enfin pour attribuer la lettre à Clément VI.');
INSERT INTO note (id,type_id,user_id,content) VALUES (517,0,'jpilla','Les Trinitaires ont été introduits à Paris en 1229, date à laquelle l’évêque Guillaume d’Auvergne leur confia un hôpital en ruine, près des Thermes ; la chapelle en était dédiée à saint Mathurin, dont le nom fut pris par le couvent, puis attribué par l’usage aux frères, dits « Mathurins ».');
INSERT INTO note (id,type_id,user_id,content) VALUES (518,0,'jpilla','L’ordre des Trinitaires (ordo sancte Trinitatis et captivorum/pro redemptione captivorum) a vu ses bases jetées par Jean de Matha vers 1195. Il était voué à la charité et tout spécialement au rachat des captifs chrétiens en terre d’Islam.');
INSERT INTO note (id,type_id,user_id,content) VALUES (519,0,'jpilla','L’ordre des Trinitaires (ordo sancte Trinitatis et captivorum/pro redemptione captivorum) a vu ses bases jetées par Jean de Matha vers 1195. Il était voué à la charité et tout spécialement au rachat des captifs chrétiens en terre d’Islam.');
INSERT INTO note (id,type_id,user_id,content) VALUES (520,0,'jpilla','Clément VI a été consacré le 19 mai 1342. La deuxième année de son pontificat courant du 19 mai 1343 au 18 mai 1344, l’acte est du 23 mars 1344.');
INSERT INTO note (id,type_id,user_id,content) VALUES (521,0,'jpilla','Mention hors-teneur indiquant le responsable du grossoiement de la lettre ; les deux personnages apparaissent régulièrement sur les lettres de l’époque ; la mention signale que le second a dû se substituer, pour une raison inconnue, au premier, qui avait été chargé du travail par la chancellerie.');
INSERT INTO note (id,type_id,user_id,content) VALUES (522,0,'jpilla','L’ordinal n’est donné que par la bulle de plomb. En l’absence de celle-ci, il faudrait croiser itinéraire pontifical, traits paléographiques et diplomatiques, données historiques enfin pour attribuer la lettre à Clément VI.');
INSERT INTO note (id,type_id,user_id,content) VALUES (523,0,'jpilla','Les Trinitaires ont été introduits à Paris en 1229, date à laquelle l’évêque Guillaume d’Auvergne leur confia un hôpital en ruine, près des Thermes ; la chapelle en était dédiée à saint Mathurin, dont le nom fut pris par le couvent, puis attribué par l’usage aux frères, dits « Mathurins ».');
INSERT INTO note (id,type_id,user_id,content) VALUES (524,0,'jpilla','L’ordre des Trinitaires (ordo sancte Trinitatis et captivorum/pro redemptione captivorum) a vu ses bases jetées par Jean de Matha vers 1195. Il était voué à la charité et tout spécialement au rachat des captifs chrétiens en terre d’Islam.');
INSERT INTO note (id,type_id,user_id,content) VALUES (525,0,'jpilla','Clément VI a été consacré le 19 mai 1342. La deuxième année de son pontificat courant du 19 mai 1343 au 18 mai 1344, l’acte est du 23 mars 1344.');
