INSERT INTO note (id,type_id,user_id,content) VALUES (531,0,'jpilla','Cette lettre ne peut avoir été écrite par Jean II le Bon que durant sa  première captivité à Londres (mai 1357-juin 1360), à la suite de la défaite de l’armée française à Poitiers en 1356, puisque son second séjour à Londres n’a duré que du 3 janvier au 8 avril 1364, date de sa mort. On peut donc proposer la date du 19 juillet 1357, 1358 ou 1359.');
