INSERT INTO note (id,type_id,user_id,content) VALUES (548,0,'jpilla','Henri II Plantagenet arrache de haute lutte la succession de la dynastie normande d’Angleterre fondée par Guillaume le Conquérant, dont il descend par sa mère. Comte d’Anjou, il est reconnu comme duc de Normandie en 1151, duc d’Aquitaine en 1152, roi d’Angleterre en 1154. Il meurt en 1189.');
INSERT INTO note (id,type_id,user_id,content) VALUES (549,0,'jpilla','Ce n’est que sous Jean sans Terre que les ethniques seront remplacés dans la titulature des rois anglais par les noms de pays (Anglie, Normannie, etc.).');
