INSERT INTO note (id,type_id,user_id,content) VALUES (551,0,'jpilla','Henri Ier Beauclerc, l’un des fils de Guillaume le Conquérant, roi d’Angleterre (1100-1135) et duc de Normandie depuis 1106, dont la succession a été revendiquée par Geoffroy Plantagenet, qui avait épousé sa fille Mathilde, puis par leur fils Henri II.');
INSERT INTO note (id,type_id,user_id,content) VALUES (552,0,'jpilla','Moulins-la-Marche (Orne, ch.-l. cant.).');
