INSERT INTO note (id,type_id,user_id,content) VALUES (555,0,'jpilla','Rotrou de Warwick, évêque d’Évreux (1139-1164), puis archevêque de Rouen jusqu’à sa mort en 1183, fut un proche du roi, pour qui il remplit de nombreuses missions et dont il fut en 1182 exécuteur testamentaire.');
INSERT INTO note (id,type_id,user_id,content) VALUES (556,0,'jpilla','Hamelin, frère naturel d’Henri II, avait épousé en 1164 Isabelle, fille de Guillaume III comte de Waren et de Surrey. Il resta fidèle au roi lors du grand soulèvement de 1173.');
INSERT INTO note (id,type_id,user_id,content) VALUES (557,0,'jpilla','Robert de Leicester peut être Robert II de Beaumont, comte de Leicester et justicier d’Angleterre, ou bien Robert III, qui succéda à son père en 1168 ; il devint en 1173 chef de file des barons révoltés et vit ses biens confisqués ; il revint en grâce en 1177.');
INSERT INTO note (id,type_id,user_id,content) VALUES (558,0,'jpilla','Richard de Hommet, issu d’un lignage de fidèles des ducs de Normandie, fut connétable de Normandie jusqu’en 1178.');
