INSERT INTO note (id,type_id,user_id,content) VALUES (581,0,'jpilla','Louis VII, roi de France de 1137 à 1180, duc d’Aquitaine du chef de son épouse Aliénor de 1137 à 1152.');
INSERT INTO note (id,type_id,user_id,content) VALUES (582,0,'jpilla','Adélaïde de Maurienne, épouse du roi Louis VI depuis 1115, mère de Louis VII, morte en 1154.');
INSERT INTO note (id,type_id,user_id,content) VALUES (583,0,'jpilla','Adelende la Gente, l’une des quelques laïques non nobles parmi les mieux connus de l’entourage royal. Épouse d’Obizon, médecin de Louis VI, dont elle est séparée par jugement royal quand celui-ci entre dans les ordres (il sera chanoine de Notre-Dame et de Saint-Victor), elle appartient sans doute à l’entourage de la reine Adélaïde. Sa fille homonyme est aussi connue par quelques actes.');
INSERT INTO note (id,type_id,user_id,content) VALUES (584,0,'jpilla','Les Champeaux, dont le nom indique l’origine rustique, sont le lieu des futures Halles, dont les bâtiments fixes ne sont pas encore construits, et qui seront intégrés au territoire urbain lors de l’édification de l’enceinte de Philippe Auguste.');
INSERT INTO note (id,type_id,user_id,content) VALUES (585,0,'jpilla','Hôte : nom générique ici donné au tenancier, occupant de la maison.');
INSERT INTO note (id,type_id,user_id,content) VALUES (586,0,'jpilla','Formule stéréotypée d’annonce des souscriptions (factices) des grands officiers. L’ordre de souscription ici suivi s’est imposé dans les années 1120.');
INSERT INTO note (id,type_id,user_id,content) VALUES (587,0,'jpilla','Raoul de Vermandois, cousin germain de Louis VI, sénéchal de France de 1131 à sa mort en 1152, avec diverses vacances entre ces deux dates. Le sénéchal n’était pas responsable que de l’armée royale, mais encore de la justice et de la gestion domaniale.');
INSERT INTO note (id,type_id,user_id,content) VALUES (588,0,'jpilla','Guillaume « le Bouteiller » de Senlis, déjà en fonction sous Louis VI, reste en fonction jusqu’au départ en croisade le 9 juin 1147. Sa famille, formée de riches chevaliers urbains, occupe la fonction de bouteiller depuis 1112 au moins et finit par en tirer son patronyme. Comme son nom l’indique, le bouteiller est spécialement chargé de l’approvisionnement de la cour en vin.');
INSERT INTO note (id,type_id,user_id,content) VALUES (589,0,'jpilla','Hugues le Borgne, connétable de 1118 à 1138. Le connétable, aux fonctions assez modestes jusqu’à la vacance du dapiférat, dirige les écuries royales, les maréchaux et les palefreniers, s’occupant donc des aspects matériels de l’organisation militaire.');
INSERT INTO note (id,type_id,user_id,content) VALUES (590,0,'jpilla','Le chambrier Hugues, déjà en fonction sous Louis VI, disparaît des actes avant le 2 avril 1138. Il dirige la «chambre», ce qui l’amène à garder le trésor comme la personne du roi.');
INSERT INTO note (id,type_id,user_id,content) VALUES (591,0,'jpilla','La formule de «délivrance par la main» du chancelier apparaît dans les diplômes royaux à compter de 1108 et s’impose à compter des années 1112/1113 ; elle n’est guère plus qu’un décalque de la formule de «grande date» des actes pontificaux.');
INSERT INTO note (id,type_id,user_id,content) VALUES (592,0,'jpilla','Algrin a déjà été chancelier sous Louis VI, en 1127-1128 sans le titre, en 1132, puis à la fin du règne, et le demeure jusqu’en 1140. Il a été, avec plus de suite, chancelier de l’église cathédrale de Paris de 1124 à sa mort en 1156/1157.');
INSERT INTO note (id,type_id,user_id,content) VALUES (593,0,'jpilla','Louis VII, roi de France de 1137 à 1180, duc d’Aquitaine du chef de son épouse Aliénor de 1137 à 1152.');
INSERT INTO note (id,type_id,user_id,content) VALUES (594,0,'jpilla','Adélaïde de Maurienne, épouse du roi Louis VI depuis 1115, mère de Louis VII, morte en 1154.');
INSERT INTO note (id,type_id,user_id,content) VALUES (595,0,'jpilla','Adelende la Gente, l’une des quelques laïques non nobles parmi les mieux connus de l’entourage royal. Épouse d’Obizon, médecin de Louis VI, dont elle est séparée par jugement royal quand celui-ci entre dans les ordres (il sera chanoine de Notre-Dame et de Saint-Victor), elle appartient sans doute à l’entourage de la reine Adélaïde. Sa fille homonyme est aussi connue par quelques actes.');
INSERT INTO note (id,type_id,user_id,content) VALUES (596,0,'jpilla','Les Champeaux, dont le nom indique l’origine rustique, sont le lieu des futures Halles, dont les bâtiments fixes ne sont pas encore construits, et qui seront intégrés au territoire urbain lors de l’édification de l’enceinte de Philippe Auguste.');
INSERT INTO note (id,type_id,user_id,content) VALUES (597,0,'jpilla','Hôte : nom générique ici donné au tenancier, occupant de la maison.');
INSERT INTO note (id,type_id,user_id,content) VALUES (598,0,'jpilla','Formule stéréotypée d’annonce des souscriptions (factices) des grands officiers. L’ordre de souscription ici suivi s’est imposé dans les années 1120.');
INSERT INTO note (id,type_id,user_id,content) VALUES (599,0,'jpilla','Formule stéréotypée d’annonce des souscriptions (factices) des grands officiers. L’ordre de souscription ici suivi s’est imposé dans les années 1120.');
INSERT INTO note (id,type_id,user_id,content) VALUES (600,0,'jpilla','Raoul de Vermandois, cousin germain de Louis VI, sénéchal de France de 1131 à sa mort en 1152, avec diverses vacances entre ces deux dates. Le sénéchal n’était pas responsable que de l’armée royale, mais encore de la justice et de la gestion domaniale.');
INSERT INTO note (id,type_id,user_id,content) VALUES (601,0,'jpilla','Guillaume «le Bouteiller» de Senlis, déjà en fonction sous Louis VI, reste en fonction jusqu’au départ en croisade le 9 juin 1147. Sa famille, formée de riches chevaliers urbains, occupe la fonction de bouteiller depuis 1112 au moins et finit par en tirer son patronyme. Comme son nom l’indique, le bouteiller est spécialement chargé de l’approvisionnement de la cour en vin.');
INSERT INTO note (id,type_id,user_id,content) VALUES (602,0,'jpilla','Hugues le Borgne, connétable de 1118 à 1138. Le connétable, aux fonctions assez modestes jusqu’à la vacance du dapiférat, dirige les écuries royales, les maréchaux et les palefreniers, s’occupant donc des aspects matériels de l’organisation militaire.');
INSERT INTO note (id,type_id,user_id,content) VALUES (603,0,'jpilla','Le chambrier Hugues, déjà en fonction sous Louis VI, disparaît des actes avant le 2 avril 1138. Il dirige la «chambre», ce qui l’amène à garder le trésor comme la personne du roi.');
