INSERT INTO note (id,type_id,user_id,content) VALUES (662,0,'jpilla','Les formes de pronom personnel sujet de personne 3  féminin relevées dans ce texte sont majoritairement "illi" (l. 22) mais on trouve aussi "illh" (voir aussi l. 6).');
INSERT INTO note (id,type_id,user_id,content) VALUES (663,0,'jpilla','Après "son",  une lettre "i" de remplissage de fin de ligne a été exponctuée par le scribe puis barrée d’un trait de plume.');
INSERT INTO note (id,type_id,user_id,content) VALUES (664,0,'jpilla','Le scribe, sur cette page, utilise "e" coordonnant devant un mot commençant par une consonne et l’abréviation "7" devant un mot commençant par une voyelle. Ailleurs dans le manuscrit il lui arrive d’utiliser "e" devant un mot commençant par une voyelle (f. 25 "e en caritat") et "7" devant un mot commençant par une consonne (f. 27v "7 par aisso"). Nous résolvons donc l’abréviation "7" par "e".');
