INSERT INTO note (id,type_id,user_id,content) VALUES (845,0,'jpilla','Guillaume II Talvas comte de Ponthieu, a pris possession des domaines normands hérités de son père Robert de Bellême en 1119. Il meurt en 1171 après avoir cédé le Ponthieu à son fils Guy II, mais en ayant gardé ses terres normandes et le titre comtal, ce qui fixe le terminus ad quem de l’acte. Le terminus a quo peut être cherché du côté de la dédicace du prieuré de Sainte-Barbe par l’évêque de Sées, le 29 mai 1157.');
INSERT INTO note (id,type_id,user_id,content) VALUES (846,0,'jpilla','Au sens anglo-normand, les forestiers ne sont pas seulement chargés des espaces boisés, mais de toutes les terres (essentiellement mais pas exclusivement non cultivées) mises hors de l’usage commun au profit du seigneur.');
INSERT INTO note (id,type_id,user_id,content) VALUES (847,0,'jpilla','Almenêches, Orne, cant. Mortré.');
