INSERT INTO note (id,type_id,user_id,content) VALUES (849,0,'jpilla','La Cochère, Orne, cant. Exmes. Le qualificatif Salta encore accolé au toponyme renvoie à un espace sauvage (saltus), exploité pour la cueillette, le pâturage (herbagium), la paisson des porcs (pasnagium).');
INSERT INTO note (id,type_id,user_id,content) VALUES (850,0,'jpilla','Robertus de Nulleio/Nullaio est un témoin fréquent des chartes du comte Guillaume.');
