INSERT INTO note (id,type_id,user_id,content) VALUES (860,0,'jpilla','Mailloles, auj. dans la banlieue de Perpignan ; le nom évoque des terrains nouvellement plantés de vignes.');
INSERT INTO note (id,type_id,user_id,content) VALUES (861,0,'jpilla','« Facta est carta » est l’incipit traditionnel de la date (« Datum est ») des chartes catalanes et plus largement ibériques  de même que « datum » a donné le mot “date”, « facta » a donné en castillan “fecha”.');
INSERT INTO note (id,type_id,user_id,content) VALUES (862,0,'jpilla','Louis VI, roi de France (1108-1137). La concordance avec l’année 1116 implique que le rédacteur prend comme point de départ du règne, et sans erreur, la mort du roi Philippe Ier (29 juillet 1108), voire le sacre de Louis VI (3 août 1108), terme usuel à la chancellerie royale.');
INSERT INTO note (id,type_id,user_id,content) VALUES (863,0,'jpilla','Le laudamentum/lods est fondamentalement une confirmation (souvent payante), donné par le seigneur à une aliénation ; le versement qui sanctionne l’accord (donc l’engagement) du censitaire est ici un droit d’entrée, assez élevé.');
INSERT INTO note (id,type_id,user_id,content) VALUES (864,0,'jpilla','Elne (Pyrénées-Orientales, ch-l. cant.). Le diocèse couvrait le comté primitif de Roussillon, mais aussi le Conflent et une partie de la Cerdagne.');
