INSERT INTO note (id,type_id,user_id,content) VALUES (983,0,'jpilla','Les « catteux » (latin capitalia) sont des biens individuels principalement mobiliers. Le terme est synonyme de meubles et s’oppose à heritage ou erve qui désignent dans les textes le patrimoine immobilier. Ces deux catégories de biens sont très différenciées par le droit coutumier qui, par exemple, définit des règles successorales distinctes pour chacune.');
