INSERT INTO note (id,type_id,user_id,content) VALUES (1032,0,'jpilla','En 1276, le bailli de Cotentin est Chrétien le Chambellan (1274-1286), originaire du diocèse de Bayeux et vicomte de Caen en 1269. Mais son nom n’est pas donné : il agit ex officio, comme le ferait un official.');
INSERT INTO note (id,type_id,user_id,content) VALUES (1033,0,'jpilla','Mortain (Manche, ch.-l. cant.), 36 km E d’Avranches. L’expression vise le couvent dit « des Blanches » (Albae dominae), moniales installées en 1105 par le comte de Mortain puis affiliées à l’ordre cistercien.');
INSERT INTO note (id,type_id,user_id,content) VALUES (1034,0,'jpilla','tournois, l’une des deux grandes monnaies royales, à l’origine frappée à Tours, et saisie par Philippe Auguste avec les possessions plantagenet.');
INSERT INTO note (id,type_id,user_id,content) VALUES (1035,0,'jpilla','Célébrée le 29 septembre, la Saint-Michel est particulièrement fêtée en Normandie, où elle constitue un terme de paiement très fréquent. La précision du mois distingue la fête d’autres, Apparition (8 mai), et surtout Dédicace de l’église du Mont-Saint-Michel (16 novembre).');
