INSERT INTO note (id,type_id,user_id,content) VALUES (1144,0,'jpilla','Le « convent » (qui a donné « couvent ») est l’ensemble des moines ou  moniales d’un monastère, en opposition/complémentarité à l’abbé ou abbesse  (ils ont vite deux sceaux différents) ; le droit canonique impose à l’abbé ou abbesse de s’assurer du consentement du convent dans les actes de gestion.');
