INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (40,40,'jpilla','Les gens des comptes du roy n<ex>ost</ex>re s<ex>eigneur</ex> a Pariset les co<ex>m</ex>missaires et generaulz gouv<ex>er</ex>neurs de toutes les finan<ex>ces</ex> dud<ex>it</ex> s<ex>eigneur</ex>tant en Langue d’oïl co<ex>m</ex>me en Langue d’oc, . .au bailli de Vermendois, aus receveur et procureur du roy n<ex>ost</ex>re s<ex>eigneur</ex> yllec,salut. Veues par nous les l<ex>ett</ex>res royaulz cy attach<ex>ees</ex>soubz l’un de noz signez, par lesqueles et pour les causes co<ex>n</ex>tenuesen ycelles le roy n<ex>ost</ex>redit s<ex>eigneur</ex> a donné, cedé <ex>et</ex> transportéa mons<ex>eigneur</ex> le conte de Charrolois et a madame Michielle deFrance, sa compaigne, et a ch<ex>asc</ex>un d’eulz les chasteaulz,villes et chastellenies de Peronne, de Roye <ex>et</ex> Mondider, pourd’iceulz lieux, ensemble de touz les droiz, fruis, prouffiz,rentes, revenues et emolumens quelzconques quey avoit et povoit avoir ledit s<ex>eigneur</ex>, tant en demaine <ex>et</ex> aides co<ex>m</ex>me aut<ex>re</ex>ment, joÿr <ex>et</ex> user plainement et paisibl<ex>ement</ex>jusques a ce que lesdiz conjoincts soient entierement paiezet contentez a une foiz de la so<ex>m</ex>me de C<hi rend="sup">M</hi> escusa eulz deue de reste et aussi de certains joyaulxet estoremens a eulz promis et convencez par le roy n<ex>ost</ex>redit s<ex>eigneur</ex>au traictié de leur mariage, co<ex>m</ex>me plus a pl<ex>ain</ex>est contenu esdictes l<ex>ett</ex>res ; et pour considerac<ex>i</ex>on du contenu enycelles, nous vous mandons, <ex>et</ex> a ch<ex>asc</ex>un de voussi co<ex>m</ex>me a lui app<ex>ar</ex>tiendra, que lesd<ex>ictes</ex> l<ex>ett</ex>res <ex>et</ex> le contenu en icell<ex>es</ex>vous ent<ex>er</ex>inez <ex>et</ex> acompliss<ex>ez</ex> de point en pointselon leur forme <ex>et</ex> teneur, tout en la maniere que le royn<ex>ost</ex>redit s<ex>eigneur</ex> le veult <ex>et</ex> mande par ycelles